<?php
// Configuração dos diretórios
$videoDir = 'uploads/videos/';
$imageDir = 'uploads/images/';
$reportFile = 'reports.json';

// Criar os diretórios se não existirem
if (!is_dir($videoDir)) mkdir($videoDir, 0777, true);
if (!is_dir($imageDir)) mkdir($imageDir, 0777, true);

// Função para listar arquivos com filtros
function listFiles($dir, $search = null, $sort = 'recent') {
    $files = is_dir($dir) ? array_diff(scandir($dir), array('.', '..')) : [];
        if ($search) {
                $files = array_filter($files, function ($file) use ($search) {
                            return stripos($file, $search) !== false;
                                    });
                                        }
                                            if ($sort === 'recent') {
                                                    usort($files, function ($a, $b) use ($dir) {
                                                                return filemtime($dir . $b) - filemtime($dir . $a);
                                                                        });
                                                                            } elseif ($sort === 'name') {
                                                                                    sort($files);
                                                                                        }
                                                                                            return $files;
                                                                                            }

                                                                                            // Função para registrar denúncias
                                                                                            function reportFile($file) {
                                                                                                global $reportFile;
                                                                                                    $reports = file_exists($reportFile) ? json_decode(file_get_contents($reportFile), true) : [];
                                                                                                        $reports[] = ['file' => $file, 'date' => date('Y-m-d H:i:s')];
                                                                                                            file_put_contents($reportFile, json_encode($reports, JSON_PRETTY_PRINT));
                                                                                                            }

                                                                                                            // Registrar denúncia
                                                                                                            if (isset($_GET['report'])) {
                                                                                                                reportFile($_GET['report']);
                                                                                                                    echo "<script>alert('Denúncia registrada para o arquivo: {$_GET['report']}');</script>";
                                                                                                                    }

                                                                                                                    // Processar upload
                                                                                                                    if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_FILES['file'])) {
                                                                                                                        $uploadDir = $_POST['type'] === 'video' ? $videoDir : $imageDir;
                                                                                                                            $fileName = basename($_FILES['file']['name']);
                                                                                                                                $uploadFile = $uploadDir . $fileName;

                                                                                                                                    if (move_uploaded_file($_FILES['file']['tmp_name'], $uploadFile)) {
                                                                                                                                            echo "<script>alert('Arquivo enviado com sucesso: $fileName');</script>";
                                                                                                                                                } else {
                                                                                                                                                        echo "<script>alert('Erro ao enviar o arquivo: $fileName');</script>";
                                                                                                                                                            }
                                                                                                                                                            }

                                                                                                                                                            // Configuração de filtros
                                                                                                                                                            $search = isset($_GET['search']) ? $_GET['search'] : null;
                                                                                                                                                            $sort = isset($_GET['sort']) ? $_GET['sort'] : 'recent';

                                                                                                                                                            // Carregar vídeos e imagens com filtros
                                                                                                                                                            $videos = listFiles($videoDir, $search, $sort);
                                                                                                                                                            $images = listFiles($imageDir, $search, $sort);
                                                                                                                                                            ?>
